import React, { Component } from "react";
import { styled, Box, Link, TextField, Typography } from "@material-ui/core";

const StyledTextField = styled(TextField)`
  background-color: #ffffff;
  padding: 7px 14px;
  border-bottom: 0px;
  &:hover {
    background-color: #5469d4;
  }
`;

const StyledTypography = styled(Typography)`
  color: #aaaaaa;
  padding: 7px 14px;
`;

const StyledHeading = styled(Typography)({
  fontSize: "20px",
  fontWeight: "bold",
  textAlign: "center",
  color: "#5C5F76",
});

class Update extends Component {
  render() {
    return (
      <div className="update">
        <StyledHeading variant="h3">Stay up to date!</StyledHeading>
        <div className="col s2"></div>

        <Box>
          <Box>
            <StyledTextField
              id="email"
              label="Email"
              variant="outlined"
            ></StyledTextField>
          </Box>
          <Link
            style={{
              color: "white",
              fontWeight: "bold",
              fontSize: "150%",
              paddingLeft: "5%",
            }}
          >
            Subscribe
          </Link>
        </Box>
        <div className="col s2"></div>
      </div>
    );
  }
}

export default Update;
